mapper.select:
select T4.*
from T_USER_ROLE T
         JOIN T_ROLE T1 ON T.role_id = T1.id
         JOIN T_APP_ROLE T2 ON T1.ID = T2.role_id
         JOIN T_ROLE_RES T3 ON T1.ID = T3.role_id
         JOIN T_RES T4 ON T4.ID = T3.res_id
WHERE T.user_id = #{userId}
  AND T2.app_id = #{appId}
  AND T1.type = #{roleType}

select DISTINCT T4.permission_code
from T_USER_ROLE T
         JOIN T_ROLE T1 ON T.role_id = T1.id
         JOIN T_APP_ROLE T2 ON T1.ID = T2.role_id
         JOIN T_ROLE_RES T3 ON T1.ID = T3.role_id
         JOIN T_RES T4 ON T4.ID = T3.res_id
WHERE AND T4.permission_code is not null

  AND T.user_id = #{userId}


  AND T2.app_id = #{appId}


  AND T1.type = #{roleType}

select DISTINCT T4.permission_code from T_USER_ROLE T JOIN T_ROLE T1 ON T.role_id = T1.id JOIN T_ROLE_RES T3 ON T1.ID = T3.role_id JOIN T_RES T4 ON T4.ID = T3.res_id WHERE T4.permission_code is not null
AND T.user_id = #{userId}
AND T1.type = #{roleType}
